import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { batchAPI, studentAPI, markAPI } from "../services/api";

const Dashboard = () => {
  const navigate = useNavigate();

  const [stats, setStats] = useState({
    totalBatches: 0,
    activeBatches: 0,
    totalStudents: 0,
    activeStudents: 0,
    totalMarks: 0,
  });
  const [recentBatches, setRecentBatches] = useState([]);
  const [recentStudents, setRecentStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) navigate("/"); 
  }, [navigate]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const [batchesRes, studentsRes, marksRes] = await Promise.all([
        batchAPI.getAll(),
        studentAPI.getAll(),
        markAPI.getAll(),
      ]);

      const batches = batchesRes.data.data;
      const students = studentsRes.data.data;
      const marks = marksRes.data.data;

      setStats({
        totalBatches: batches.length,
        activeBatches: batches.filter((b) => b.isActive).length,
        totalStudents: students.length,
        activeStudents: students.filter((s) => s.isActive).length,
        totalMarks: marks.length,
      });

      setRecentBatches(batches.slice(0, 5));
      setRecentStudents(students.slice(0, 5));
    } catch (err) {
      console.error("Failed to fetch dashboard data:", err);
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ Logout
  const handleLogout = () => {
  localStorage.removeItem("token");
  window.location.href = "/";
};


  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="container" style={{ marginTop: "30px" }}>
      {/* Header with logout */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "30px",
        }}
      >
        <h1>Dashboard</h1>
        <button
          onClick={handleLogout}
          style={{
            backgroundColor: "#ff4b4b",
            color: "white",
            padding: "10px 20px",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Logout
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-3" style={{ marginBottom: "30px" }}>
        <div
          className="card"
          style={{
            background:
              "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
            color: "white",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "start",
            }}
          >
            <div>
              <p style={{ marginBottom: "10px", opacity: 0.9 }}>Total Batches</p>
              <p style={{ fontSize: "36px", fontWeight: "bold", marginBottom: "5px" }}>
                {stats.totalBatches}
              </p>
              <p style={{ fontSize: "14px", opacity: 0.8 }}>
                {stats.activeBatches} Active
              </p>
            </div>
            <div style={{ fontSize: "40px", opacity: 0.3 }}>üìö</div>
          </div>
          <Link to="/batches">
            <button
              className="btn"
              style={{
                marginTop: "15px",
                background: "rgba(255,255,255,0.2)",
                color: "white",
                width: "100%",
              }}
            >
              View All Batches
            </button>
