};

// ‚úÖ Component for Recent Lists
const RecentList = ({ title, items, type, link }) => (
  <div className="card">
    <div
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: "20px",
      }}
    >
      <h2>{title}</h2>
      <Link to={link}>
        <button
          className="btn btn-secondary"
          style={{ fontSize: "12px", padding: "5px 15px" }}
        >
          View All
        </button>
      </Link>
    </div>
    {items.length === 0 ? (
      <p style={{ color: "#6b7280", textAlign: "center", padding: "20px" }}>
        No {type}s found
      </p>
    ) : (
      <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        {items.map((item) => (
          <Link
            to={`/${type}s/${item._id}`}
            key={item._id}
            style={{ textDecoration: "none" }}
          >
            <div
              style={{
                padding: "15px",
                background: "#f9fafb",
                borderRadius: "5px",
                transition: "all 0.3s ease",
                cursor: "pointer",
                border: "1px solid #e5e7eb",
              }}
              onMouseOver={(e) =>
                (e.currentTarget.style.background = "#f3f4f6")
              }
              onMouseOut={(e) =>
                (e.currentTarget.style.background = "#f9fafb")
              }
            >
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <div>
                  {type === "batch" ? (
                    <>
                      <p
                        style={{
                          fontWeight: "600",
                          color: "#111827",
                          marginBottom: "5px",
                        }}
                      >
                        {item.batchName}
                      </p>
                      <p style={{ fontSize: "12px", color: "#6b7280" }}>
                        Code: {item.batchCode} ‚Ä¢ {item.totalStudents} Students
                      </p>
                    </>
                  ) : (
                    <>
                      <p
                        style={{
                          fontWeight: "600",
                          color: "#111827",
                          marginBottom: "5px",
                        }}
                      >
                        {item.firstName} {item.lastName}
                      </p>
                      <p style={{ fontSize: "12px", color: "#6b7280" }}>
                        {item.enrollmentNumber} ‚Ä¢ {item.batch?.batchName}
                      </p>
                    </>
                  )}
                </div>
                <span
                  className={`badge ${
                    item.isActive ? "badge-success" : "badge-danger"
                  }`}
                >
                  {item.isActive ? "Active" : "Inactive"}
                </span>
              </div>
            </div>
          </Link>
        ))}
      </div>
    )}
  </div>
);

export default Dashboard;
